import { View,Text } from "react-native"
import { FlatList,Pressable } from "react-native";
import { useState } from "react";
import  Accordion  from  'react-native-collapsible/Accordion' ;

export default function Todo(){
    const Data =[ 
        { 
          title : 'Pertama' , 
          content : 'Lorem ipsum...' , 
        } , 
        { 
          title : 'Kedua' , 
          content : 'Lorem ipsum...' , 
        } , 
      ] ;
        
        const todo =({item})=>{
            return(<View style={{borderRadius:5,
                                paddingBottom:10,
                                marginLeft:20,
                                flex:1,
                                flexDirection:'row',
                                justifyContent:'space-between',
                                backgroundColor:'gray',
                                marginTop:10,
                                width:350}}>
                <Pressable style={{marginLeft:20,marginTop:20}}>
                    <Text style={{fontSize:15,fontWeight:'bold',color:'black'}}>{item.title}</Text>
                </Pressable>
                <Pressable style={{marginRight:15,backgroundColor:'green',borderRadius:5, width:50,height:30,marginTop:10}}>
                    <Text style={{marginLeft:7,color:'white',marginTop:5,fontWeight:'bold'}}>Done</Text>
                </Pressable>
            </View>
            )
        }
    const header =({item})=>{
        return(
            <Text>{item.title}</Text>
        )
    }
    const content =({item})=>{
        return(
            <Text>{item.content}</Text>
        )
    }
    return(
        <View>
        < Accordion 
            activeSections = { [ 0 ] } 
            section = {Data} 
            renderSectionTitle = {todo} 
            renderHeader = {header} 
            renderContent = {content}
        / > 
        </View>
    )
}
