import { View,Text } from "react-native"
import { FlatList,Pressable } from "react-native";
import { useState } from "react";
import ModalDescription from '../components/modal'

export default function Todo(){
    const [modal, setModal]= useState('false')
    const Data =[
        {
            id: 'bd7acbea-c1b1-46c2-aed5-3ad53abb28ba',
            title: 'First Item',
          },
          {
            id: '3ac68afc-c605-48d3-a4f8-fbd91aa97f63',
            title: 'Second Item',
          },
          {
            id: '58694a0f-3da1-471f-bd96-145571e29d72',
            title: 'Third Item',
          },
        ];
        
        const todo =({item})=>{
            return(<>
            <ModalDescription show = {modal}/>
            <View style={{borderRadius:5,
                                paddingBottom:10,
                                marginLeft:20,
                                flex:1,
                                flexDirection:'row',
                                justifyContent:'space-between',
                                backgroundColor:'gray',
                                marginTop:10,
                                width:350}}>
                <Pressable onPress={()=>setModal(true)} style={{marginLeft:20,marginTop:20}}>
                    <Text style={{fontSize:15,fontWeight:'bold',color:'black'}}>{item.title}</Text>
                </Pressable>
                <Pressable style={{marginRight:15,backgroundColor:'green',borderRadius:5, width:50,height:30,marginTop:10}}>
                    <Text style={{marginLeft:7,color:'white',marginTop:5,fontWeight:'bold'}}>Done</Text>
                </Pressable>
            </View>
            </>
            )
        }
    
    return(
        <View>
            
            <FlatList
                data={Data}
                renderItem={todo}
                keyExtractor={item => item.id}
            />
        </View>
    )
}
