import React, {useState} from 'react';
import {
  SafeAreaView,

  ScrollView,
  StyleSheet,
  Text,
  View,Pressable
 } from 'react-native';
import * as Animatable from 'react-native-animatable';
import Accordion from 'react-native-collapsible/Accordion';
import axios from 'axios'
import { useEffect } from 'react';

const Todo = () => {

    const [post, setPost] = useState([])
    const [isLoading, setIsLoading] = useState(false)
    useEffect(()=>{
        getPost()
        console.log('e')
    },[])

    const getPost = () => {
        setIsLoading(true)
        axios
            .get('https://61e26aba3050a10017682175.mockapi.io/todo')
            .then((res)=>{
                setPost(res.data)
                setIsLoading(false)
            })
            .catch(()=> {
                alert('Error Fetch Data')
                setIsLoading(false)
            })
    }

    console.log(post)
    const newPost = post.filter((item)=>item.status == 'not yet')

    
  const [activeSections, setActiveSections] = useState([]);
  const setSections = (sections) => {
      setActiveSections(
      sections.includes(undefined) ? [] : sections
    );
  };
  const renderHeader = (section, _, isActive) => {
      return (
      <Animatable.View
        duration={400}
        style={[
          styles.header,
          isActive ? styles.active : styles.inactive
        ]}
        transition="backgroundColor">
        <Text style={styles.headerText}>
          {section.title}
        </Text>
      </Animatable.View>
    );
  };

  const renderContent = (section, _, isActive) => {
    const handleSubmit = async() => {
        console.log(section.id)
        const config ={
          headers:{
            "Content-type": "application/json",
          },
        };
        const data ={status:'finish'}
         const res = await axios.put(`http://192.168.43.190:5000/api/v1/todo/${section.id}`, data,config);
      };
    return (
      <Animatable.View
        duration={400}
        style={[
          styles.content,
          isActive ? styles.active : styles.inactive
        ]}
        transition="backgroundColor">
        <Text><span style={{fontWeight:'bold'}}>Description:</span>{section.description}</Text>
        <Text style={{marginTop:20}}><span style={{fontWeight:'bold'}}>Time:</span>{section.time}</Text>
        <Pressable style={styles.btn} onPress={handleSubmit}><Text style={styles.btntxt}>Done</Text></Pressable>
      </Animatable.View>
    );
  };
  return (
    <SafeAreaView style={{flex: 1}}>
      <View style={styles.container}>
        <ScrollView>
        <Accordion
            activeSections={activeSections}
            sections={newPost}
            renderHeader={renderHeader}
            renderContent={renderContent}
            duration={400}
            onChange={setSections}
          />

        </ScrollView>
      </View>
    </SafeAreaView>
  );
};

export default Todo;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: 20,
    marginLeft:20
  },
  headerText: {
    textAlign: 'left',
    marginLeft:20,
    fontSize: 14,
    fontWeight: 'bold',
    color:'black',marginTop:5
  },
  content: {
    padding: 20,
    backgroundColor: '#fff',
    color:'black'
  },
  active: {
    backgroundColor: 'white',width:330,
  },
  inactive: {
    backgroundColor: 'gray',
  },
  header:{
      color:'white',
      marginTop:10,
      width:330,
      height:30,
      borderRadius:5
  },
  btn :{
    marginLeft:250,backgroundColor:'green',borderRadius:5, width:50,height:30,marginTop:20
  },
  btntxt:{
    marginLeft:7,color:'white',marginTop:5,fontWeight:'bold'
  }
});
